import { useState, useEffect } from 'react' 

function RecipeCard(rec) {
    return (
        <div className="recipe-card">
            <img className="recipe-pic" src={rec.Picture} alt={rec.Title} />
            <p>{rec.Title}</p>
            <p>{rec.Description}</p>
        </div>
    )
}

function getRecipeData() {
    async function getData() {
        try{
            const response = await fetch("http://localhost:8080/recipe/latest");
            if (response.ok) {
                return response.json();
            } else {
                const error = await response.json();
                displayMessage("error: ", error.error);
                return error;
            }
        } catch(error) {
            displayMessage("500 internal server error", "there was an error contacting the server")
            return response.json();
        }
    }

    return getData();
}

function RenderBody() {

}

function Body() {
    const [ recipeData, setRecipeData ] = useState([])

    useEffect(() => {
        async function fetchData() {
            const data = await getRecipeData();
            setRecipeData(data) 
        }

        fetchData();
    }, [])

    console.log(recipeData);
    recipeData.forEach(element => {
        console.log(element.Title)
    });
    return(
        <>
            {recipeData.map(recipe => (
                <RecipeCard key={recipe.id} {...recipe} />
            ))}
        </>
    )
}

export default Body;
